$nuget = resolve-path "$env:SENTICODE_CI_TOOLS\common\nuget\*\nuget.exe" | Select -ExpandProperty Path
& $nuget pack src\Senticode.XFT.MVVM\Senticode.Xamarin.Tools.MVVM.csproj -Build -Properties Configuration=Release -Properties PackageId=Senticode.Xamarin.Tools.MVVM -Properties Version="$env:ASSEMBLY_VERSION" -Properties Description="The tools provide components for MVVM architecture in Xamarin.Forms applications" -Properties Author="Eugene Malaschuk" -Properties Company=Senticode -IncludeReferencedProjects
& $nuget pack src\Senticode.XFT.UI\Senticode.Xamarin.Tools.UI.csproj -Build -Properties Configuration=Release -Properties PackageId=Senticode.Xamarin.Tools.UI -Properties Version="$env:ASSEMBLY_VERSION" -Properties Description="The tools provide components for MVVM architecture in Xamarin.Forms applications" -Properties Authors="Eugene Malaschuk" -Properties Company=Senticode -IncludeReferencedProjects
